import { useState } from 'react'
import Header from "./components/Header";
import General from "./components/Inputs/General";
import Educational from "./components/Inputs/Educational";
import PracticalExp from "./components/Inputs/PracticalExp";
import PersonalCV from "./components/PersonalCV";

function App() {
  const [infos, setInfos] = useState({
    id: 1,
    firstname: 'George',
    lastname: 'Walker',
    role: 'MEP Manager',
    email: 'fake@addy.com'
});

  // Set Experience
  const [exps, setExps] = useState([{
    id: 2,
    role: 'MEP Manager',
    company: 'Multiplex Construction Europe',
    dateFrom: 'Apr 2019',
    dateTo: 'Present',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'
  },{
    id: 3,
    role: 'MEP Manager',
    company: 'Multiplex Construction Europe',
    dateFrom: 'Apr 2019',
    dateTo: 'Present',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'
  }]);

  // Set Education
  const [edus, setEdu] = useState([{
    id: 4,
    school: 'uol',
    level: 'Bachelors Degree',
    courseSubject: 'Engineering',
    dateFrom: 'Apr 2019',
    dateTo: 'Mar 2020'
  },{
    id: 5,
    school: 'birkbeck',
    level: 'Masterse Degree',
    courseSubject: 'Politics',
    dateFrom: 'Apr 2020',
    dateTo: 'May 2021'
  }]);


  // Add Exp
  const addExp = (exp) => {
    const id = Math.floor(Math.random() * 10000 + 1)
    const newExp = {id, ...exp}
    setExps([...exps, newExp])
  }

  // Delete Exps
  const deleteExps = (id) => {
    setExps(exps.filter((exp)=> exp.id !== id))
  }

  // Add Education
  const addEdu = (edu) => {
    const id = Math.floor(Math.random() * 10000 + 1)
    const newEdu = {id, ...edu}
    setEdu([...edus, newEdu])
  }

  // Delete Education
  const deleteEdu = (id) => {
    setEdu(edus.filter((edu)=> edu.id !== id))
  }

  return (
    <div className='container'>
      <Header />
      <div className='flex'>
        <General />
        <Educational onAdd={addEdu}/>
        <PracticalExp onAdd={addExp}/>
        <PersonalCV edus={edus} infos={infos} exps={exps} onDelete={deleteExps , deleteEdu}/>
      </div>
    </div>
  );
}

export default App;
