import { faTasks } from "@fortawesome/free-solid-svg-icons";
import { useState } from 'react';


const exps = [{
    id: 1,
    role: 'MEP Manager',
    date: 'Apr 2019 - Present',
    description: 'Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.'
}];

const PersonalCV = () => {
    const [infos, setState] = useState({
        id: 1,
        firstname: 'George',
        lastname: 'Walker',
        role: 'MEP Manager',
        email: 'fake@addy.com'
    })
    return (
        <div className='cv-container flex card'>
            <div className='name-header flex'>
                <h1 key={infos.id}>{infos.firstname} {infos.lastname}</h1>
                <h2 key={infos.id}>{infos.role}</h2>
            </div>
            <div className='cv-body'>
                <div className='grid'>
                    <div className='experience my-1'>
                        <h2>Experience</h2>
                        {exps.map((exp) => (
                        <h3 key={exp.id}>{exp.role}</h3>
                         ))}
                        {exps.map((exp) => (
                        <h3 key={exp.id}>{exp.date}</h3>
                         ))}
                         {exps.map((exp) => (
                        <h3 key={exp.id}>{exp.description}</h3>
                         ))}
                    </div>
                    <div className='personal-description my-1'>
                        <h2>Personal Brief</h2>
                        <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat. Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur. Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.</p>
                    </div>
                    <div className='education my-1'>
                        <h2>Education</h2>
                        <h3>University</h3>
                        <h3>Degree</h3>
                        <h4>Date</h4>
                    </div>
                </div>
            <div className='references flex'>
                <h2>More Information</h2>
            </div>
            </div>
        </div>
    )
}

export default PersonalCV
